mapper.insert:
INSERT INTO BASKET_STAY_ACCOM(BASKET_CODE
, SUB_ACCOM_CODE
, MEMBER_ID
, STAY_START_DATE
, STAY_END_DATE
, STAY_TERM
, DAY_USE)
VALUES((SELECT 'BAS_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(BASKET_CODE,5))),0)+1,3,'0')FROM BASKET_STAY_ACCOM)
, #{subAccomCode}
, #{memberId}
, #{stayStartDate}
, #{stayEndDate}
, #{stayTerm}
, #{dayuse})

mapper.update:
