SET
REFUND = #{refund}
, REVIEW=#{review}
, REFUND_PRICE=#{refundPrice}

    , REFUND_DATE=TO_DATE(#{refundDate},'YYYY-MM-DD HH24:MI:SS')


    WHERE RESERVATION_STATE_CODE = #{reservationStateCode}


    WHERE RESERVATION_CODE = #{reservationCode}

mapper.select:
