mapper.insert:
INSERT INTO JEJU_MEMBER (
MEMBER_ID
, MEMBER_PW
, MEMBER_NAME
, MEMBER_TEL
, GENDER
, BIRTH_DATE
, MEMBER_MAIL
) VALUES (
#{memberId}
, #{memberPw}
, #{memberName}
,

    #{tel}

, #{gender}
, #{birthDate}
, #{member_mail}
)

mapper.select:
SELECT MEMBER_ID
, MEMBER_NAME
, MEMBER_ROLL
FROM JEJU_MEMBER
WHERE MEMBER_ID = #{memberId}
AND MEMBER_PW = #{memberPw}

SELECT MEMBER_ID
FROM JEJU_MEMBER
WHERE MEMBER_ID = #{memberId}

mapper.update:
UPDATE JEJU_MEMBER
SET
MEMBER_MAIL = #{memberMail}
,MEMBER_TEL = #{memberTel}
WHERE MEMBER_ID = #{memberId}

UPDATE JEJU_MEMBER
SET
MEMBER_PW = #{memberPw}
WHERE MEMBER_ID = #{memberId}

mapper.select:
SELECT MEMBER_ID
FROM JEJU_MEMBER
WHERE MEMBER_MAIL = #{member_mail}

mapper.update:
UPDATE JEJU_MEMBER
SET
MEMBER_PW = #{memberPw}
WHERE MEMBER_ID = #{memberId}
